#include<iostream>#include<time.h>#include<random>#include<math.h>#include "Position.hpp"using namespace std;int main() {	Position t1, t2;	double l, u, px, py, pz, x2, y2, z2, d2, d3;	cout << "Enter lower and upper range for first point: ";	cin >> l;	cin >> u;	cout << "Enter x, y, z for second point:" << endl;	cin >> x2;	cin >> y2;	cin >> z2;	t2.setValue(x2, y2, z2);	t1.setRandom(l, u);	t1.getValue(px, py, pz);	d2 = t2.distance2D(t1);	d3 = t2.distance3D(t1);}